         {
             type = free
         }
     }

    @PART[rkekarm600]
     {
         %MODULE[TweakScale]
         {
             type = free
         }
     }

    @PART[rkekelbow]
     {
         %MODULE[TweakScale]
         {
             type = free
         }
     }

    @PART[rkeklee]
     {
         %MODULE[TweakScale]
         {
             type = free
         }
     }

    @PART[rkeklee]
     {MODULE{	name = KASModuleMagnet	breakForce = 10	attachToEva = False	minFwdDot = 0.9961947	powerDrain = 4}
     }

    @PART[rkeklee]
