> 
> 
> 22 minutes ago, Hohmannson said:
> 
> 
> It certainly does. Which TUFX antialiasing mode do you use? I want mine look that good too.
> 

I use KS3P with custom config:

Spoiler

KS3P
    {
        Profile
        {
            name = MAIN
            author = EEX
                    scene = MainMenu, SpaceCenter, VAB, SPH, TrackingStation, Flight, EVA, IVA, MapView
            Anti_Aliasing
            {
                fxaa_preset = ExtremeQuality
            }
            Ambient_Occlusion
            {
                ambient_only = True
                enabled = False
            }
            Depth_Of_Field
            {
                aperture = 4.08
                focal_length = 103
                use_camera_fov = True
                enabled = False
            }
            Motion_Blur
            {
                frame_blending = 0.239
                sample_count = 4
                shutter_angle = 1
                enabled = False
            }
            Eye_Adaptation
            {
                adaptation_type = Fixed
                high_percent = 92.17
                key_value = 0.05
                log_max = 16
                log_min = -4
                low_percent = 8.1
                max_luminance = 8
                enabled = False
            }
            Bloom
            {
                bloom_anti_flicker = True
                bloom_intensity = 1.2
                bloom_radius = 1.81
                bloom_soft_knee = 0.75		bloom_threshold = 1.2
            }
            Color_Grading
            {
                Basic
                {
                    contrast = 1.1
                    saturation = 1.1
                }
                Mixer
                {
                }
                Wheels
                {
                    mode = Log
                }
                Curves
                {
                    Master
                    {
                        key = 0, 0, 1, 1
                        key = 1, 1, 1, 1
                    }
                    Red
                    {
                        key = 0, 0, 1, 1
                        key = 1, 1, 1, 1
                    }
                    Green
                    {
                        key = 0, 0, 1, 1
                        key = 1, 1, 1, 1
                    }
                    Blue
                    {
                        key = 0, 0, 1, 1
                        key = 1, 1, 1, 1
                    }
                    Hue_Versus_Hue
                    {
                        zero = 0.5
                        loop = True
                    }
                    Hue_Versus_Saturation
                    {
                        zero = 0.5
                        loop = True
                    }
                    Luminosity_Versus_Saturation
                    {
                        zero = 0.5
                    }
                    Saturation_Versus_Saturation
                    {
                        zero = 0.5
                    }
                }
                Tonemapper
                {
                }
            }
            User_Lut
            {
                lut = KS3P/Textures/Fallback
                enabled = False
            }
            Chromatic_Abberation
            {
                intensity = 0.08
                texture = KS3P/Textures/SpectralLut_BlueRed
            }
            Grain
            {
                colored = False
                intensity = 0.252
                luminance_contribution = 1
                enabled = False
            }
            Vignette
            {
                mask = KS3P/Textures/Fallback
                rounded = True
                enabled = False
            }
            Screen_Space_Reflection
            {
                enabled = False
            }
        }
    }
