[Here](https://github.com/Angel-125/Pathfinder/blob/e19a2289b715db4d14a0f74353ffa0c903f83103/GameData/WildBlueIndustries/Pathfinder/Templates/Production/SunburnLab.cfg) is the link to the proper config for the Sunburn Lab module and the DeepFreeze converter on Angel-125's github; search for DeepFreeze and you'll find it (it's at the bottom)

    @PATH_SCIENCE[Sunburn]:FOR[Pathfinder]:NEEDS[DeepFreeze]
    {MODULE{	name = ModuleResourceConverter	ConverterName = Glykerol	StartActionName = Start Glykerol	StopActionName = Stop Glykerol	AutoShutdown = false	GeneratesHeat = false	UseSpecialistBonus = true	SpecialistEfficiencyFactor = 0.2	SpecialistBonusBase = 0.05	Specialty = Engineer	EfficiencyBonus = 1 	INPUT_RESOURCE	{		ResourceName = Coolant		Ratio = 0.01		FlowMode = STAGE_PRIORITY_FLOW	}INPUT_RESOURCE	{		ResourceName = RareMetals		Ratio = 0.001		FlowMode = STAGE_PRIORITY_FLOW	}INPUT_RESOURCE	{		ResourceName = ExoticMinerals		Ratio = 0.001		FlowMode = STAGE_PRIORITY_FLOW	}INPUT_RESOURCE	{		ResourceName = ElectricCharge		Ratio = 30	}OUTPUT_RESOURCE	{		ResourceName = Glykerol		Ratio = .0025		DumpExcess = false		FlowMode = STAGE_PRIORITY_FLOW	}}
    RESOURCE{	name = Glykerol	amount = 600	maxAmount = 600	isTweakable = true}
    }
