        RPM\_COLOROVERRIDE  

        {  

        // MFD -------------------------------------------------------------------------------------------------------------------------------------------------------

        // button backlight color

            COLORDEFINITION  

            {  

                name = ASET\_MFD\_BACKLIGHT\_ACTIVECOLOR // passive color of tumble and rotary/dial switcher name label  

                color = 53,60,69,255  

            }  

        // switchers  

            COLORDEFINITION  

            {  

                name = ASET\_SWITCHER\_NAME\_POSITIVECOLOR  

                color= 140,160,180,255  

            }  

            COLORDEFINITION  

            {  

                name = ASET\_SWITCHER\_STATE\_POSITIVECOLOR  

                color= 120,140,255,255  

            }  

            COLORDEFINITION  

            {  

                name = ASET\_SWITCHER\_BORDER\_POSITIVECOLOR  

                color= 100,120,155,255  

            }  

            COLORDEFINITION  

            {  

                name = ASET\_SWITCHER\_MARK\_POSITIVECOLOR  

                color= 100,120,185,255  

            }  

        // digital indicator --------------------------------  

        // readout  

            COLORDEFINITION  

            {  

                name = ASET\_DIGITALINDICATOR\_NORMAL\_COLOR  

                color= 180,210,255,255  

            }  

            COLORDEFINITION  

            {  

                name = ASET\_DIGITALINDICATOR\_WARNING\_COLOR  

                color= 255,140,20,255  

            }  

            COLORDEFINITION  

            {  

                name = ASET\_DIGITALINDICATOR\_STBY\_COLOR  

                color= 100,120,155,255  

            }  

