                color = 255,255,255,255  

            }  

            COLORDEFINITION  

            {  

                name = ASET\_FM\_VERTBAR\_FUELTOT\_ACTIVECOLOR //light on  

                color = 200,200,255,255  

            }  

            COLORDEFINITION  

