    suffix = m  

    scaleFactors   = 0.1,  0.3125, 0.625, 0.9375, 1.25, 1.875, 2.5, 3.125, 3.75, 5.0, 7.5, 10, 20  

    incrementSlide = 0.01, 0.025, 0.025, 0.025, 0.025, 0.025, 0.05, 0.05, 0.05, 0.1, 0.1, 0.2  

    TWEAKSCALEEXPONENTS { mass = 2 }  

}

